import React, { useState } from "react";
import axios from "axios";

const ForgotPassword: React.FC = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const response = await axios.post("http://localhost:3010/api/auth/forgot-password", { email });
      setMessage(`‚úÖ ${response.data.message}`);
    } catch (error: any) {
      if (error.response) {
        setMessage(`‚ùå ${error.response.data.message}`);
      } else {
        setMessage("‚ùå Erreur r√©seau. V√©rifiez votre connexion.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h2>üîê R√©initialisation du mot de passe</h2>
      <p>Entrez votre adresse e-mail pour recevoir un lien de r√©initialisation.</p>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="Votre email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? "Envoi en cours..." : "Envoyer"}
        </button>
      </form>
      {message && <p className="mt-3">{message}</p>}
    </div>
  );
};

export default ForgotPassword;
