import React, { createContext, useState, useEffect, ReactNode } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";

const API_URL = "http://localhost:3010/api/auth";

interface User {
  id: string;
  name: string;
  username: string;
  email: string;
}

interface Post {
  id: string;
  message: string; // Assurer que le champ est bien "message"
  photoUrl?: string;
  author: string;
  createdAt: string;
}

interface AuthContextType {
  user: User | null;
  posts: Post[];
  login: (email: string, password: string) => Promise<void>;
  register: (name: string, username: string, email: string, password: string) => Promise<void>;
  logout: () => void;
}

export const AuthContext = createContext<AuthContextType | null>(null);

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const navigate = useNavigate();

  const logout = () => {
    console.log("DÃ©connexion...");
    localStorage.removeItem("token");
    setUser(null);
    setPosts([]);
    navigate("/login");
  };

  const fetchPosts = async () => {
    try {
      const { data } = await axios.get(`${API_URL}/posts`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });

      console.log("ðŸ“© Posts rÃ©cupÃ©rÃ©s :", data);

      setPosts(data.map((post: any) => ({
        ...post,
        message: post.content || post.message, // S'assure que "message" est bien rÃ©cupÃ©rÃ©
      })));
    } catch (error) {
      console.error("Erreur lors du chargement des posts :", error);
    }
  };

  useEffect(() => {
    console.log("ðŸŒ€ useEffect exÃ©cutÃ© !");

    const loadUserFromToken = () => {
      const token = localStorage.getItem("token");
      if (!token) return;

      try {
        const decodedUser: any = jwtDecode(token);
        setUser({
          id: decodedUser.id,
          name: decodedUser.name,
          username: decodedUser.username,
          email: decodedUser.email,
        });
      } catch (err) {
        console.error("âŒ Token invalide ou expirÃ©:", err);
        logout();
      }
    };

    loadUserFromToken();

    if (user) {
      fetchPosts();
    }
  }, [user]); 

  const login = async (email: string, password: string) => {
    try {
      console.log("Tentative de connexion avec :", { email });

      const { data } = await axios.post(`${API_URL}/login`, { email, password }, { withCredentials: true });

      if (!data.token || !data.user) {
        throw new Error("RÃ©ponse API invalide");
      }

      localStorage.setItem("token", data.token);
      console.log("âœ… Token enregistrÃ© dans localStorage :", localStorage.getItem("token"));

      setUser(data.user);
      navigate("/profile");
    } catch (error) {
      console.error("Erreur de connexion :", error);
      alert("Erreur de connexion. Veuillez vÃ©rifier vos identifiants");
    }
  };

  const register = async (name: string, username: string, email: string, password: string) => {
    try {
      await axios.post(`${API_URL}/register`, { name, username, email, password });
      navigate("/login");
    } catch (error) {
      console.error("Erreur d'inscription :", error);
      alert("Erreur d'inscription. Veuillez essayer Ã  nouveau.");
    }
  };

  return (
    <AuthContext.Provider value={{ user, posts, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
