import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

interface Post {
  id: string;
  message: string;
  photoUrl?: string;
}

const Profile: React.FC = () => {
  const { user, posts, logout } = useContext(AuthContext) || {};
  const [localPosts, setLocalPosts] = useState<Post[]>(posts || []);
  const [postMessage, setPostMessage] = useState<string>("");
  const [postPhoto, setPostPhoto] = useState<string>("");

  useEffect(() => {
    if (posts) {
      setLocalPosts(posts);
    }
  }, [posts]); 

  const handlePostSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (postMessage.trim() === "" && postPhoto.trim() === "") return;

    const newPost: Post = {
      id: Date.now().toString(),
      message: postMessage,
      photoUrl: postPhoto || undefined,
    };

    setLocalPosts((prevPosts) => [newPost, ...prevPosts]);

    setPostMessage("");
    setPostPhoto("");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Mon espace</h1>

      <button onClick={logout}>DÃ©connexion</button>

      <h2>Votre Profil</h2>
      <p>Bienvenue, {user?.name}!</p>

      <form onSubmit={handlePostSubmit} style={{ marginBottom: "20px" }}>
        <textarea
          value={postMessage}
          onChange={(e) => setPostMessage(e.target.value)}
          placeholder="Votre message..."
          rows={4}
          cols={50}
        />
        <input
          type="text"
          value={postPhoto}
          onChange={(e) => setPostPhoto(e.target.value)}
          placeholder="URL de la photo (optionnel)"
          style={{ display: "block", marginBottom: "10px", width: "300px" }}
        />
        <button type="submit">Poster</button>
      </form>

      <h3>Publications</h3>
      {localPosts.length === 0 ? (
        <p>Aucune publication.</p>
      ) : (
        <ul>
          {localPosts.map((post) => (
            <li key={post.id} style={{ marginBottom: "10px", borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
              {post.message && <p>{post.message}</p>}
              {post.photoUrl && (
                <img src={post.photoUrl} alt="Publication" style={{ maxWidth: "200px" }} />
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
