import React, { createContext, useState, useEffect, ReactNode, useContext } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";

const API_URL = "http://localhost:3010/api/auth";

interface User {
  id: string;
  name: string;
  username: string;
  email: string;
}

interface AuthContextType {
  user: User | null;
  login: (email: string, password: string) => Promise<void>;
  register: (name: string, username: string, email: string, password: string) => Promise<void>;
  logout: () => void;
}

// ‚úÖ Cr√©ation d'un contexte avec des valeurs par d√©faut pour √©viter les erreurs
export const AuthContext = createContext<AuthContextType>({
  user: null,
  login: async () => {},
  register: async () => {},
  logout: () => {},
});

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const navigate = useNavigate();

  // ‚úÖ Fonction logout correcte
  const logout = () => {
    console.log("D√©connexion...");
    localStorage.removeItem("token");
    setUser(null);
    navigate("/home");
  };

  useEffect(() => {
    console.log("üåÄ useEffect ex√©cut√© !");

    const loadUserFromToken = () => {
      const token = localStorage.getItem("token");
      console.log("üîë Token r√©cup√©r√© :", token);

      if (token) {
        try {
          const decodedUser: any = jwtDecode(token);
          console.log("‚úÖ Utilisateur d√©cod√© :", decodedUser);

          setUser({
            id: decodedUser.id,
            name: decodedUser.name,
            username: decodedUser.username,
            email: decodedUser.email,
          });

        } catch (err) {
          console.error("‚ùå Token invalide ou expir√©:", err);
          logout();
        }
      }
    };

    loadUserFromToken();

    window.addEventListener("storage", loadUserFromToken);

    return () => {
      window.removeEventListener("storage", loadUserFromToken);
    };
  }, []); // ‚úÖ Pas besoin de d√©pendance `logout`

  // ‚úÖ Fonction login corrig√©e
  const login = async (email: string, password: string) => {
    try {
      console.log("Tentative de connexion avec :", { email });

      const { data } = await axios.post(`${API_URL}/login`, { email, password }, { withCredentials: true });

      console.log("R√©ponse API re√ßue :", data);

      if (!data.token || !data.user) {
        throw new Error("R√©ponse API invalide");
      }

      localStorage.setItem("token", data.token);
      console.log("‚úÖ Token enregistr√© dans localStorage :", localStorage.getItem("token"));

      setUser(data.user);
      navigate("/profile");
    } catch (error) {
      console.error("Erreur de connexion :", error);
      alert("Erreur de connexion. Veuillez v√©rifier vos identifiants");
    }
  };

  // ‚úÖ Fonction register corrig√©e
  const register = async (name: string, username: string, email: string, password: string) => {
    try {
      console.log("Tentative d'inscription avec :", { name });
      await axios.post(`${API_URL}/register`, { name, username, email, password });

      navigate("/login");
    } catch (error) {
      console.error("Erreur d'inscription :", error);
      alert("Erreur d'inscription. Veuillez essayer √† nouveau.");
    }
  };

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
