import React from "react";

interface SidebarProps {
  activeTab: string;
  setActiveTab: (tab: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ activeTab, setActiveTab }) => {
  return (
    <nav className="nav flex-column p-3 rounded shadow bg-light">
      {["profil", "admin", "chat", "messagerie"].map((tab) => (
        <button
          key={tab}
          className={`nav-link text-white fw-bold ${activeTab === tab ? "bg-pink" : "bg-secondary"}`}
          onClick={() => setActiveTab(tab)}
          style={{ borderRadius: "10px", marginBottom: "5px" }}
        >
          {tab.charAt(0).toUpperCase() + tab.slice(1)}
        </button>
      ))}
    </nav>
  );
};

export default Sidebar;
