import React, { useState } from "react";

const MessagesContent: React.FC = () => {
  const [newMessage, setNewMessage] = useState<string>("");
  const [messages, setMessages] = useState<string[]>([]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (newMessage.trim() === "") return;
    setMessages([...messages, newMessage]);
    setNewMessage("");
  };

  return (
    <div className="card p-4">
      <h2>Messagerie</h2>
      <form onSubmit={handleSendMessage} className="mb-3">
        <textarea className="form-control mb-2" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} placeholder="Écrivez un nouveau message..." rows={3} />
        <button type="submit" className="btn btn-primary w-100">Envoyer</button>
      </form>
      <h3>Messages envoyés</h3>
      <ul className="list-group">
        {messages.map((msg, index) => <li key={index} className="list-group-item">{msg}</li>)}
      </ul>
    </div>
  );
};

export default MessagesContent;
