import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

interface Post {
  id: number;
  message: string;
  photoUrl?: string;
}

const Profile: React.FC = () => {
  const [activeTab, setActiveTab] = useState<string>("profil");
  const [posts, setPosts] = useState<Post[]>([]);
  const [postMessage, setPostMessage] = useState<string>("");
  const [postPhoto, setPostPhoto] = useState<string>("");
  const [isProfilePublic, setIsProfilePublic] = useState<boolean>(true);
  const [newMessage, setNewMessage] = useState<string>("");
  const [messages, setMessages] = useState<string[]>([]);
  const friends = ["Alice", "Bob", "Charlie", "David"];

  useEffect(() => {
    const savedPosts = localStorage.getItem("posts");
    if (savedPosts) {
      try {
        const parsedPosts = JSON.parse(savedPosts);
        if (Array.isArray(parsedPosts)) {
          setPosts(parsedPosts);
        }
      } catch (error) {
        console.error("Erreur lors de la rÃ©cupÃ©ration des posts :", error);
      }
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("posts", JSON.stringify(posts));
  }, [posts]);

  const handlePostSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (postMessage.trim() === "" && postPhoto.trim() === "") return;
    const newPost: Post = { id: Date.now(), message: postMessage, photoUrl: postPhoto || undefined };
    setPosts([newPost, ...posts]);
    setPostMessage("");
    setPostPhoto("");
  };

  return (
    <div className="container mt-4">
      <h1 className="text-primary text-center">My World</h1>
      <nav className="nav nav-pills justify-content-center mb-4">
        {['profil', 'admin', 'chat', 'messagerie'].map(tab => (
          <button key={tab} className={`nav-link ${activeTab === tab ? 'active' : ''}`} onClick={() => setActiveTab(tab)}>
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </nav>
      {activeTab === "profil" && (
        <div className="card p-4">
          <h2>Welcome, ðŸ‘‹</h2>
          <form onSubmit={handlePostSubmit} className="mb-3">
            <textarea className="form-control mb-2" value={postMessage} onChange={(e) => setPostMessage(e.target.value)} placeholder="Tap your message..." rows={3} />
            <input type="text" className="form-control mb-2" value={postPhoto} onChange={(e) => setPostPhoto(e.target.value)} placeholder="Photo URL (optional)" />
            <button type="submit" className="btn btn-primary w-100">Post</button>
          </form>
          <h3>Posts</h3>
          {posts.length === 0 ? <p>No post.</p> : (
            <ul className="list-group">
              {posts.map((post) => (
                <li key={post.id} className="list-group-item">
                  {post.message && <p>{post.message}</p>}
                  {post.photoUrl && <img src={post.photoUrl} alt="Post" className="img-fluid" />}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
